import React, { useEffect, useState } from 'react';
import axios from '../utils/axios';
import { useNavigate } from 'react-router-dom';
import jwtDecode from 'jwt-decode';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './Dashboard.css';

toast.configure();

function Dashboard() {
    const [requests, setRequests] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [showForm, setShowForm] = useState(false);
    const [newRequest, setNewRequest] = useState({
        type: '',
        location: '',
        description: '',
        requestDate: '',
        requestTime: '',
        approvalStatus: 'PENDING',
        status: 'OPEN',
    });
    const navigate = useNavigate();

    useEffect(() => {
        const fetchRequests = async () => {
            const token = localStorage.getItem('token');
            if (!token) {
                navigate('/login');
                return;
            }

            try {
                const response = await axios.get('/requests', {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setRequests(response.data);
            } catch (err) {
                if (err.response && err.response.status === 401) {
                    navigate('/login');
                } else {
                    console.error("Error fetching requests: ", err);
                    setError('Failed to fetch requests. Please try again later.');
                }
            } finally {
                setLoading(false);
            }
        };

        fetchRequests();
    }, [navigate]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewRequest({ ...newRequest, [name]: value });
    };

    const validateForm = () => {
        if (!newRequest.type || !newRequest.location || !newRequest.description) {
            toast.error('All fields are required.');
            return false;
        }
        if (!newRequest.requestDate || !newRequest.requestTime) {
            toast.error('Please select a valid date and time.');
            return false;
        }
        return true;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!validateForm()) return;

        const token = localStorage.getItem('token');
        const decodedToken = jwtDecode(token);
        const userId = decodedToken.id;

        const combinedDateTime = `${newRequest.requestDate}T${newRequest.requestTime}`;
        const requestToSend = {
            ...newRequest,
            requestDate: combinedDateTime,
            requestedById: userId,
        };

        try {
            await axios.post('/requests', requestToSend, {
                headers: { Authorization: `Bearer ${token}` },
            });
            toast.success('Request submitted successfully!');
            setShowForm(false);
            setNewRequest({
                type: '',
                location: '',
                description: '',
                requestDate: '',
                requestTime: '',
                approvalStatus: 'PENDING',
                status: 'OPEN',
            });

            const response = await axios.get('/requests', {
                headers: { Authorization: `Bearer ${token}` },
            });
            setRequests(response.data);
        } catch (err) {
            console.error("Error creating request: ", err);
            toast.error('Failed to create request. Please try again.');
        }
    };

    if (loading) {
        return <div className="loading">Loading...</div>;
    }

    if (error) {
        return <div className="error-message">{error}</div>;
    }

    return (
        <div className="dashboard-container">
            <h1 className="dashboard-title">Maintenance Requests</h1>
            <button onClick={() => setShowForm(true)} className="btn btn-primary">
                Add Request
            </button>
            {showForm && (
                <form onSubmit={handleSubmit} className="request-form">
                    <input
                        type="text"
                        name="type"
                        placeholder="Enter Request Type"
                        value={newRequest.type}
                        onChange={handleInputChange}
                        required
                    />
                    <input
                        type="text"
                        name="location"
                        placeholder="Enter Location"
                        value={newRequest.location}
                        onChange={handleInputChange}
                        required
                    />
                    <input
                        type="text"
                        name="description"
                        placeholder="Enter Description"
                        value={newRequest.description}
                        onChange={handleInputChange}
                        required
                    />
                    <input
                        type="date"
                        name="requestDate"
                        value={newRequest.requestDate}
                        onChange={handleInputChange}
                        required
                    />
                    <input
                        type="time"
                        name="requestTime"
                        value={newRequest.requestTime}
                        onChange={handleInputChange}
                        required
                    />
                    <button type="submit" className="btn btn-success">
                        Submit Request
                    </button>
                    <button
                        type="button"
                        onClick={() => setShowForm(false)}
                        className="btn btn-secondary"
                    >
                        Cancel
                    </button>
                </form>
            )}
            <table className="table">
                <thead>
                    <tr>
                        <th>Request ID</th>
                        <th>Type</th>
                        <th>Location</th>
                        <th>Description</th>
                        <th>Request Date</th>
                        <th>Approval Status</th>
                        <th>Status</th>
                    </tr>
                </thead>
                <tbody>
                    {requests.map((request) => (
                        <tr key={request.id}>
                            <td>{request.id}</td>
                            <td>{request.type}</td>
                            <td>{request.location}</td>
                            <td>{request.description}</td>
                            <td>{new Date(request.requestDate).toLocaleString()}</td>
                            <td>{request.approvalStatus}</td>
                            <td>{request.status}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default Dashboard;
