import React, { useEffect, useState } from 'react';
import axios from '../utils/axios';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import './Dashboard.css';

function Dashboard() {
    const [requests, setRequests] = useState([]);
    const [users, setUsers] = useState({});
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        const fetchRequests = async () => {
            const token = localStorage.getItem('token');
            if (!token) {
                navigate('/login');
                return;
            }

            try {
                const response = await axios.get('/requests', {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setRequests(response.data);

                const userPromises = response.data.map(request => 
                    axios.get(`/api/users/${request.requestedById}`, {
                        headers: { Authorization: `Bearer ${token}` },
                    })
                );

                const userResponses = await Promise.all(userPromises);
                const userMap = {};
                userResponses.forEach(userResponse => {
                    const user = userResponse.data;
                    userMap[user.id] = user;
                });
                setUsers(userMap);
            } catch (err) {
                console.error("Error fetching requests or users: ", err);
                setError('Failed to fetch requests or users. Please try again later.');
            } finally {
                setLoading(false);
            }
        };

        fetchRequests();
    }, [navigate]);

    if (loading) {
        return <div className="loading">Loading...</div>;
    }

    if (error) {
        return <div className="error-message">{error}</div>;
    }

    return (
        <div className="dashboard-container">
            <h1 className="dashboard-title">Maintenance Requests</h1>
            <button onClick={() => navigate('/request-form')} className="btn btn-primary">Add Request</button>
            <table className="table">
                <thead>
                    <tr>
                        <th>Request ID</th>
                        <th>Type</th>
                        <th>Location</th>
                        <th>Description</th>
                        <th>Requested By ID</th>
                        <th>Status</th>
                        <th>User Position</th>
                    </tr>
                </thead>
                <tbody>
                    {requests.map((request) => (
                        <tr key={request.id}>
                            <td>{request.id}</td>
                            <td>{request.type}</td>
                            <td>{request.location}</td>
                            <td>{request.description}</td>
                            <td>{request.requestedById}</td>
                            <td>{request.status}</td>
                            <td>{users[request.requestedById]?.position || 'N/A'}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default Dashboard;
