import React, { useState } from 'react';
import axios from 'axios';

function RequestForm() {
    const [request, setRequest] = useState({
        type: '',
        location: '',
        description: '',
    });

    const handleChange = (e) => {
        setRequest({ ...request, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem('token');
            await axios.post('http://localhost:8080/api/requests', request, {
                headers: { Authorization: `Bearer ${token}` },
            });
            alert('Request submitted successfully!');
        } catch (error) {
            alert('Failed to submit the request.');
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <select name="type" onChange={handleChange}>
                <option value="MAINTENANCE">Maintenance</option>
                <option value="WORK">Work</option>
            </select>
            <input type="text" name="location" onChange={handleChange} placeholder="Location" />
            <textarea name="description" onChange={handleChange} placeholder="Description"></textarea>
            <button type="submit">Submit</button>
        </form>
    );
}

export default RequestForm;
