import React, { useEffect, useState } from 'react';
import axios from '../utils/axios';
import { useNavigate } from 'react-router-dom';
import './Dashboard.css';
import { jwtDecode } from 'jwt-decode';

function Dashboard() {
    const [requests, setRequests] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [showForm, setShowForm] = useState(false);
    const [newRequest, setNewRequest] = useState({ 
        type: '', 
        location: '', 
        description: '', 
        requestedById: '',
        requestDate: '',
        requestTime: '',
        approvalStatus: 'PENDING',
        status: 'OPEN'
    });
    const navigate = useNavigate();

    useEffect(() => {
        const fetchRequests = async () => {
            const token = localStorage.getItem('token');
            if (!token) {
                navigate('/login');
                return;
            }

            try {
                const response = await axios.get('/requests', {
                    headers: { Authorization: `${token}` },
                });
                setRequests(response.data);
            } catch (err) {
                console.error("Error fetching requests: ", err);
                setError('Failed to fetch requests. Please try again later.');
            } finally {
                setLoading(false);
            }
        };

        fetchRequests();
    }, [navigate]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewRequest({ ...newRequest, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const token = localStorage.getItem('token');
        const decodedToken = jwtDecode(token);
        const userId = decodedToken.id;

        // Combine requestDate and requestTime into a single LocalDateTime string
        const combinedDateTime = `${newRequest.requestDate}T${newRequest.requestTime}`;

        // Create a new request object with the combined date and time
        const requestToSend = {
            ...newRequest,
            requestDate: combinedDateTime,
            requestedById: userId,
        };

        try {
            await axios.post('/requests', requestToSend, {
                headers: { Authorization: `Bearer ${token}` },
            });
            setShowForm(false);
            setNewRequest({ 
                type: '', 
                location: '', 
                description: '', 
                requestedById: '', 
                requestDate: '',
                requestTime: '',
                approvalStatus: 'PENDING', 
                status: 'OPEN' 
            });
            const response = await axios.get('/requests', {
                headers: { Authorization: `Bearer ${token}` },
            });
            setRequests(response.data);
        } catch (err) {
            console.error("Error creating request: ", err);
            setError('Failed to create request. Please try again later.');
        }
    };

    if (loading) {
        return <div className="loading">Loading...</div>;
    }

    if (error) {
        return <div className="error-message">{error}</div>;
    }

    return (
        <div className="dashboard-container">
            <h1 className="dashboard-title">Maintenance Requests</h1>
            <button onClick={() => setShowForm(true)} className="btn btn-primary">Add Request</button>
            {showForm && (
                <form onSubmit={handleSubmit} className="request-form">
                    <input 
                        type="text" 
                        name="type" 
                        placeholder="Enter Request Type" 
                        value={newRequest.type} 
                        onChange={handleInputChange} 
                        required 
                    />
                    <input 
                        type="text" 
                        name="location" 
                        placeholder="Enter Location" 
                        value={newRequest.location} 
                        onChange={handleInputChange} 
                        required 
                    />
                    <input 
                        type="text" 
                        name="description" 
                        placeholder="Enter Description" 
                        value={newRequest.description} 
                        onChange={handleInputChange} 
                        required 
                    />
                    <input 
                        type="date" 
                        name="requestDate" 
                        placeholder="Select Request Date" 
                        value={newRequest.requestDate} 
                        onChange={handleInputChange} 
                        required 
                    />
                    <input 
                        type="time" 
                        name="requestTime" 
                        placeholder="Select Request Time" 
                        value={newRequest.requestTime} 
                        onChange={handleInputChange} 
                        required 
                    />
                    <input 
                        type="text" 
                        name="requestedById" 
                        placeholder="Enter Your User ID" 
                        value={newRequest.requestedById} 
                        onChange={handleInputChange} 
                        required 
                    />
                    <button type="submit">Submit Request</button>
                    <button type="button" onClick={() => setShowForm(false)}>Cancel</button>
                </form>
            )}
            <table className="table">
                <thead>
                    <tr>
                        <th>Request ID</th>
                        <th>Type</th>
                        <th>Location</th>
                        <th>Description</th>
                        <th>Status</th>
                    </tr>
                </thead>
                <tbody>
                    {requests.map((request) => (
                        <tr key={request.id}>
                            <td>{request.id}</td>
                            <td>{request.type}</td>
                            <td>{request.location}</td>
                            <td>{request.description}</td>
                            <td>{request.status}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default Dashboard;
